$(document).ready(function() {
  var aodLinks  = new Object();
  aodLinks.dlfFlash = "javascript:$(window).mkdraod_interface('openAODwithStream', $(window).mkdraod_interface('consts').stream.MKDRAOD_STREAM_DEUTSCHLANDFUNK);return false;".replace("&", '\u0026');
  aodLinks.dlfMp3 = 'http://www.deutschlandradio.de/streaming/dlf.m3u'.replace("&", '\u0026');
  aodLinks.dlfOgg = 'http://www.deutschlandradio.de/index.508.de.html'.replace("&", '\u0026');
  aodLinks.dlfWma = ''.replace("&", '\u0026');
  aodLinks.drkFlash = "javascript:$(window).mkdraod_interface('openAODwithStream', $(window).mkdraod_interface('consts').stream.MKDRAOD_STREAM_DEUTSCHLANDRADIO_KULTUR);return false;".replace("&", '\u0026');
  aodLinks.drkMp3 = 'http://www.deutschlandradio.de/streaming/dkultur.m3u'.replace("&", '\u0026');
  aodLinks.drkOgg = 'http://www.deutschlandradio.de/index.508.de.html'.replace("&", '\u0026');
  aodLinks.drkWma = ''.replace("&", '\u0026');
  aodLinks.drwFlash = "javascript:$(window).mkdraod_interface('openAODwithStream', $(window).mkdraod_interface('consts').stream.MKDRAOD_STREAM_DRADIO_WISSEN);return false;".replace("&", '\u0026');
  aodLinks.drwMp3 = 'http://prelaunch.dradio.de/streaming/dradiowissen.m3u'.replace("&", '\u0026');
  aodLinks.drwOgg = 'http://prelaunch.dradio.de/index.508.de.html'.replace("&", '\u0026');
  aodLinks.drwWma = ''.replace("&", '\u0026');
  var onAirLinks = new Object();
  onAirLinks.dlf = 'http://www.deutschlandfunk.de/jetzt-im-radio.1852.de.html';
  onAirLinks.drk = 'http://www.deutschlandradiokultur.de/programmvorschau.282.de.html';
  onAirLinks.drw = 'http://www.deutschlandradio.de/jetzt-im-radio.261.de.html';
  var defaultStation = '';

    $('#mkdrwr_text_more > a').prop('href', null);
    $('#mkdrwr_widget').mkdrwr_widget({
      'blendInterval': 0,
      'switchChannel': function(channel, type) {
      },
      'selectPodcast': function(channel) {
      },
      'selectRecorder': function(channel) {
      },
      'selectMediathek': function(channel) {
      },
      'selectMore': function(channel) {
        switch(channel){
          case 'funk':
            $('#mkdrwr_text_more > a').attr('href', onAirLinks.dlf);
            break;
          case 'kultur':
            $('#mkdrwr_text_more > a').attr('href', onAirLinks.drk);
            break;
          case 'wissen':
            $('#mkdrwr_text_more > a').attr('href', onAirLinks.drw);
            break;
        }
      },
      'switchType': function(type, channel) {
        switch (type) {
          case 'flash':
            $('#mkdrwr_button_funk > a').attr('href', aodLinks.dlfFlash);
            $('#mkdrwr_button_kultur > a').attr('href', aodLinks.drkFlash);
            $('#mkdrwr_button_wissen > a').attr('href', aodLinks.drwFlash);
            $('#mkdrwr_button_funk > a').attr('onclick', 'javascript:popupAOD(\'' + aodLinks.dlfFlash + ' \');return false;');
            $('#mkdrwr_button_kultur > a').attr('onclick', 'javascript:popupAOD(\'' + aodLinks.drkFlash + ' \');return false;');
            $('#mkdrwr_button_wissen > a').attr('onclick', 'javascript:popupAOD(\'' + aodLinks.drwFlash + ' \');return false;');
            break;
          case 'mp3':
            $('#mkdrwr_button_funk > a').attr('href', aodLinks.dlfMp3);
            $('#mkdrwr_button_kultur > a').attr('href', aodLinks.drkMp3);
            $('#mkdrwr_button_wissen > a').attr('href', aodLinks.drwMp3);
            $('#mkdrwr_button_funk > a').prop('onclick', null);
            $('#mkdrwr_button_kultur > a').prop('onclick', null);
            $('#mkdrwr_button_wissen > a').prop('onclick', null);
            break;
          case 'ogg':
            $('#mkdrwr_button_funk > a').attr('href', aodLinks.dlfOgg);
            $('#mkdrwr_button_kultur > a').attr('href', aodLinks.drkOgg);
            $('#mkdrwr_button_wissen > a').attr('href', aodLinks.drwOgg);
            $('#mkdrwr_button_funk > a').prop('onclick', '');
            $('#mkdrwr_button_kultur > a').prop('onclick', '');
            $('#mkdrwr_button_wissen > a').prop('onclick', '');
            break;
          case 'wmp':
            $('#mkdrwr_button_funk > a').attr('href', aodLinks.dlfWma);
            $('#mkdrwr_button_kultur > a').attr('href', aodLinks.drkWma);
            $('#mkdrwr_button_wissen > a').attr('href', aodLinks.drwWma);
            $('#mkdrwr_button_funk > a').prop('onclick', null);
            $('#mkdrwr_button_kultur > a').prop('onclick', null);
            $('#mkdrwr_button_wissen > a').prop('onclick', null);
            break;
        }
      }
    });

  $('#mkdrwr_widget').mkdrwr_widget('setTextState', {
    'funk':broadcastDataDlf
  });
  $('#mkdrwr_widget').mkdrwr_widget('setTextState', {
    'kultur':broadcastDataDrk
  });
  $('#mkdrwr_widget').mkdrwr_widget('setTextState', {
    'wissen':broadcastDataDrw
  });
  if(defaultStation != '') {
    switch (defaultStation) {
      case 'DLF':
        $('#mkdrwr_widget').mkdrwr_widget('setButtonState', 'funk');
        break;
      case 'DRK':
        $('#mkdrwr_widget').mkdrwr_widget('setButtonState', 'kultur');
        break;
      case 'DRW':
        $('#mkdrwr_widget').mkdrwr_widget('setButtonState', 'wissen');
        break;
    }
  }
});

$('a[data-audio-src]').each(function() {
  var attr = $(this).attr('href');
  if (typeof attr === 'undefined' || attr === false) {
    $(this).attr('href', '#');
  }
  $(this).click(function(event) {
    event.preventDefault();
  });
});

